DROP TABLE IF EXISTS movieland.movie;

CREATE TABLE IF NOT EXISTS movieland.movie
(
    id bigint NOT NULL GENERATED ALWAYS AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    title character varying(300) COLLATE pg_catalog."default" NOT NULL,
    issue_year integer NOT NULL,
    plot character varying(10000) COLLATE pg_catalog."default" NOT NULL,
    rating decimal(2,1) NOT NULL,
    price decimal(7,2) NOT NULL,
    CONSTRAINT movie_pkey PRIMARY KEY (id),
    CONSTRAINT movie_title UNIQUE (title, issue_year)    
);

ALTER TABLE movieland.movie OWNER to jacksparrow;

