DROP TABLE IF EXISTS movieland.review;

CREATE TABLE IF NOT EXISTS movieland.review
(
    movie_id bigint NOT NULL,
    user_id bigint NOT NULL,
    post_time timestamp NOT NULL,
    text character varying(10000) COLLATE pg_catalog."default" NOT NULL,
    FOREIGN KEY (movie_id) REFERENCES movie (id),
    FOREIGN KEY (user_id) REFERENCES "user" (id),
    CONSTRAINT review_unique UNIQUE (movie_id, user_id, post_time)
);

ALTER TABLE movieland.review OWNER to jacksparrow;

